import react, { useEffect } from "react";
import { useTimer } from 'react-compound-timer';

export default function Timer(props) {
  const {
    value,
    controls: { setTime },
  } = useTimer({ initialTime: 0 });

  useEffect(() => {
    setTime(props.initialTime);
  }, [props.initialTime]);
  return (
    <div>
      <h2>{value.m}</h2>
      <h2>{value.s}</h2>
    </div>
  );
}
