/* eslint-disable no-console */
/* eslint-disable no-plusplus */
/* eslint-disable no-restricted-syntax */
import moment from 'moment';

const sort = (weekData) => {
  const sorter = {
    sunday: 0,
    monday: 1,
    tuesday: 2,
    wednesday: 3,
    thursday: 4,
    friday: 5,
    saturday: 6,
  };

  weekData.sort((a, b) => {
    const day1 = a.day.toLowerCase();
    const day2 = b.day.toLowerCase();
    return sorter[day1] - sorter[day2];
  });
};

const nextIrrigation = (weekData, roundsData) => {
  console.log(weekData);
  if (!weekData || !roundsData) {
    return 'Error';
  }
  sort(weekData);
  const nextSevenDays = [];
  for (let i = 0; i < 7; i++) {
    nextSevenDays[i] = moment().add(i, 'days').format('dddd');
    let nextIrrigationTime = moment(new Date(), 'HH:mm:ss');
    let nextIrrigationRound;
    if (weekData[nextSevenDays[i].toLowerCase()] === true) {
      for (const [, round] of Object.entries(roundsData)) {
        const startTimeMoment = moment(round.startTime, 'HH:mm:ss');
        if (
          (round.isActive &&
            i === 0 && // i = 0 - today
            startTimeMoment.isAfter(moment(new Date(), 'HH:mm:ss'))) ||
          (round.isActive && i !== 0)
        ) {
          if (startTimeMoment.isBefore(nextIrrigationTime)) {
            nextIrrigationTime = startTimeMoment;
            nextIrrigationRound = round;
          }
        }
      }
      if (nextIrrigationRound) {
        const times = nextIrrigationRound.startTime.split(':');
        return `${nextSevenDays[i]} ${times[0]}:${times[1]}`;
      }
    }
  }
  return 'Not set';
};

export default nextIrrigation;
