/* eslint-disable no-console */
/* eslint-disable no-unused-expressions */
import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';
import Timer, { useTimer } from 'react-compound-timer';

import './Manual.css';

function Manual(props) {
  const [textButton, setTextColor] = useState('START');
  const [minutes, setMinutes] = useState(0);
  const [loading, setLoading] = useState('determinate');
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(false);
  const {
    value,
    controls: { setTime, start, stop },
  } = useTimer({ initialTime: 0 });

  useEffect(() => {
    setTime(props.initialTime);
  }, [props.initialTime]);

  const { user } = props;

  const clickButton = (event) => {
    event.preventDefault();
    setLoading('indeterminate');
    setIsSubmitDisabled(true);
    if (textButton === 'START') {
      axios
        .post(`${user.data().dataplicity}/irrigate_by_minutes/${minutes}`) // DO NOT KIP 1 MINUTE HARDCODED!!
        .then(() => {
          setTextColor('STOP');
          console.log('irrigation started');
        })
        .catch((error) => {
          console.log(error);
        })
        .finally(() => {
          setLoading('determinate');
          setIsSubmitDisabled(false);
        });
    } else {
      axios
        .post(`${user.data().dataplicity}/stop_irrigate`)
        .then(() => {
          setTextColor('START');
          console.log('irrigation stopped');
        })
        .catch((error) => {
          console.log(error);
          console.log("IRRIGATION DIDN'T STOPPTED!!!");
        })
        .finally(() => {
          setLoading('determinate');
          setIsSubmitDisabled(false);
        });
    }
  };

  return (
    <form className="manual-irrigation-form" onSubmit={clickButton}>
      <div className="minutes-input-wrapper">
        <TextField
          type="number"
          id="minutes-input"
          label="Minutes"
          variant="outlined"
          value={minutes}
          required
          onChange={(event) => {
            setMinutes(event.target.value);
          }}
        />
      </div>
      <Timer
        className="timer"
        initialTime={minutes * 60 * 1000}
        direction="backward"
        startImmediately={false}
        formatValue={(val) => `${val < 10 ? `0${val}` : val}`}
        lastUnit="h"
        timeToUpdate={1000}
      >
        {() => (
          <>
            <div className="breathing-button-wrapper">
              <Button
                id="breathing-button"
                className="manual-button"
                variant="contained"
                color={textButton === 'START' ? 'primary' : 'secondary'}
                type="submit"
                disabled={isSubmitDisabled}
                onClick={textButton === 'START' ? start : stop}
              >
                {textButton}
              </Button>
            </div>
            <div className="timer-wrapper" hidden={textButton === 'START'}>
              <Timer.Hours />:
              <Timer.Minutes />:
              <Timer.Seconds />
            </div>
          </>
        )}
      </Timer>
      <div className="loader-wrapper">
        <CircularProgress className="loader" variant={loading} />
      </div>
    </form>
  );
}

export default Manual;
