/* eslint-disable no-lone-blocks */
/* eslint-disable no-unused-vars */
import React, { useContext, useState } from 'react';

import CircularProgress from '@material-ui/core/CircularProgress';
import RefreshIcon from '@material-ui/icons/Refresh';
import IconButton from '@material-ui/core/IconButton';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';

import { UserContext } from '../providers/UserProvider';

// import WatermingButton from '../components/WateringButton';
import './Home.css';

const useStyles = makeStyles((theme) => ({
  hidden: {
    display: 'none',
  },
  listItem: {
    backgroundColor: 'rgba(232, 241, 247, 0.795)',
    cursor: 'unset',
    borderRadius: '5px',
  },
  stopButton: {
    backgroundColor: 'rgba(255, 172, 151, 0.795)',
    padding: '0px',
    textAlign: 'center',
    marginLeft: '3px',
  },
  buttonAppear: {
    animation: `$appear 1000ms`,
    width: '64px',
    // display: 'block',
  },
  buttonDisappear: {
    animation: `$appear 1000ms reverse`,
    width: '0px',
    // display: 'none',
  },
  '@keyframes appear': {
    '0%': {
      width: '0px',
      // display: 'none',
    },
    '100%': {
      width: '64px',
      // display: 'block',
    },
  },
}));

const Home = (props) => {
  const user = useContext(UserContext);
  const classes = useStyles();
  const {
    status,
    errorMessage,
    loader,
    getStatus,
    turnOffWatering,
    setStatus,
  } = props;

  const getHelloMessage = () => {
    const today = new Date();
    const curHr = today.getHours();
    if (curHr < 12) {
      return 'Good morning';
    }
    if (curHr < 18) {
      return 'Good afternoon';
    }
    if (curHr < 21) {
      return 'Good evening';
    }
    return 'Good night';
  };

  return (
    <>
      <div className="main-body">
        <div className="container">
          <div className="container-header">{`${getHelloMessage()} ${
            user.firstName
          }`}</div>
          <div className="container-body">
            <div className="left-side">
              <List component="nav" aria-label="main mailbox folders">
                <div className="status-list-wrapper">
                  <ListItem className={classes.listItem} button>
                    <ListItemText primary={`Status: ${status}`} />
                    <p className="status-error-message">{errorMessage}</p>
                    <IconButton size="small" onClick={getStatus}>
                      {loader ? (
                        <CircularProgress size={25} />
                      ) : (
                        <RefreshIcon
                          style={{ color: 'rgb(191 191 191 / 91%)' }}
                        />
                      )}
                    </IconButton>
                  </ListItem>
                  <ListItem
                    className={clsx(
                      classes.listItem,
                      classes.stopButton,
                      // status === 'OFF' && classes.buttonDisappear,
                      // status === 'ON' && classes.buttonAppear,
                    )}
                  >
                    <Button onClick={turnOffWatering}>
                      <ListItemText primary="STOP" />
                    </Button>
                  </ListItem>
                </div>
                <Divider className="divider" />
                <ListItem className={classes.listItem} button>
                  <ListItemText primary="Next Irrigation:" />
                </ListItem>
              </List>
              <Button
                onClick={() => {
                  setStatus(status === 'OFF' ? 'ON' : 'OFF');
                }}
              >
                Click
              </Button>
              {/* <div className="status-text-wrapper">
              <p className="status-text">{`Status: ${status}`}</p>
              <IconButton size="small">
                <RefreshIcon />
              </IconButton>
            </div> */}

              {/* <WatermingButton
              isBreathing={isBreathing}
              textButton={
                isSubmitDisabled ? (
                  <CircularProgress className="status-loader" size="10" />
                ) : (
                  <RefreshIcon fontSize="large" style={{ color: '#cecbcb' }} />
                )
              }
              isSubmitDisabled={isSubmitDisabled}
              onClick={getStatus}
            /> */}
              {/* <p className="status-error-message">{errorMessage}</p> */}
            </div>
          </div>
        </div>
        {/* <hr orientation="vertical" />
        <div className="container right">
          <div className="container-header">Watering status</div>
          <div className="container-body">
            <div className="current-status-wrapper">
              <p className="current-status">Currently:{` ${status}`}</p>
            </div>
            <p className="status-error-message">{errorMessage}</p>
          </div>
        </div> */}
      </div>
    </>
  );
};

export default Home;
