/* eslint-disable no-loop-func */
/* eslint-disable consistent-return */
/* eslint-disable no-plusplus */
/* eslint-disable no-restricted-syntax */
import moment from 'moment';

const nextIrrigation = (weekData, roundsData) => {
  if (!weekData || !roundsData) {
    return 'Error';
  }
  const nextSevenDays = [];
  const irrigationStartHours = Object.keys(roundsData).map((round) =>
    moment(roundsData[round].startTime, 'HH:mm:ss'),
  );
  const sortedStartHours = irrigationStartHours.sort(
    (a, b) => a.valueOf() - b.valueOf(),
  );
  let minTime = sortedStartHours[0];
  const now = moment(new Date(), 'HH:mm:ss');

  for (let i = 0; i < 7; i++) {
    nextSevenDays[i] = moment().add(i, 'days').format('dddd');
    if (weekData[nextSevenDays[i].toLowerCase()]) {
      if (minTime.isAfter(now)) {
        console.log(minTime);
        return `${nextSevenDays[i]} ${minTime.format('HH:mm')}`;
      }

      if (i === 0) {
        // its today
        let isSet = false;
        sortedStartHours.forEach((startHour) => {
          if (startHour.isAfter(now)) {
            isSet = true;
            minTime = startHour;
          }
        });
        if (isSet) {
          return `${nextSevenDays[i]} ${minTime.format('HH:mm')}`;
        }
      }
      return `${nextSevenDays[i]} ${minTime.format('HH:mm')}`;
    }
  }
  return 'Not set';
};

export default nextIrrigation;
