/* eslint-disable no-lone-blocks */
/* eslint-disable no-unused-vars */
import React, { useState, useContext, useEffect } from 'react';

import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';
import RefreshIcon from '@material-ui/icons/Refresh';
import IconButton from '@material-ui/core/IconButton';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';

import { UserContext } from '../providers/UserProvider';

import WatermingButton from '../components/WateringButton';
import './Home.css';

const Home = () => {
  const user = useContext(UserContext);

  const [status, setStatus] = useState('OFF');
  const [isBreathing, setIsBreathing] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [loader, setLoader] = useState(false);

  const errorHandler = (customError) => {
    setErrorMessage(customError || 'There was a problem loading the data');
  };

  const getStatus = () => {
    const { urlCode } = user;
    setErrorMessage('');
    setLoader(true);
    axios
      .get(`${urlCode}/get_state`, {
        timeout: 10 * 1000,
      })
      .then((response) => {
        const { data } = response;
        if (data) {
          setStatus('ON');
          setIsBreathing(true);
        } else {
          setStatus('OFF');
          setIsBreathing(false);
        }
      })
      .catch((error) => {
        errorHandler('Connection error');
        console.error(error);
      })
      .finally(() => {
        setLoader(false);
      });
  };

  const getHelloMessage = () => {
    const today = new Date();
    const curHr = today.getHours();
    if (curHr < 12) {
      return 'Good morning';
    }
    if (curHr < 18) {
      return 'Good afternoon';
    }
    return 'Good evening';
  };

  return (
    <>
      <div className="main-body">
        <div className="container">
          <div className="container-header">{`${getHelloMessage()} ${
            user.firstName
          }`}</div>
          <div className="container-body">
            <List component="nav" aria-label="main mailbox folders">
              <ListItem className="list-item" button>
                <ListItemText primary={`Status: ${status}`} />
                <IconButton size="small" onClick={getStatus}>
                  {loader ? (
                    <CircularProgress size={25} />
                  ) : (
                    <RefreshIcon style={{ color: '#cecbcb' }} />
                  )}
                </IconButton>
              </ListItem>
              <Divider className="divider" />
              <ListItem className="list-item" button>
                <ListItemText primary="Next Irrigation:" />
              </ListItem>
            </List>
            {/* <div className="status-text-wrapper">
              <p className="status-text">{`Status: ${status}`}</p>
              <IconButton size="small">
                <RefreshIcon />
              </IconButton>
            </div> */}

            {/* <WatermingButton
              isBreathing={isBreathing}
              textButton={
                isSubmitDisabled ? (
                  <CircularProgress className="status-loader" size="10" />
                ) : (
                  <RefreshIcon fontSize="large" style={{ color: '#cecbcb' }} />
                )
              }
              isSubmitDisabled={isSubmitDisabled}
              onClick={getStatus}
            /> */}
            {/* <p className="status-error-message">{errorMessage}</p> */}
          </div>
        </div>
        {/* <hr orientation="vertical" />
        <div className="container right">
          <div className="container-header">Watering status</div>
          <div className="container-body">
            <div className="current-status-wrapper">
              <p className="current-status">Currently:{` ${status}`}</p>
            </div>
            <p className="status-error-message">{errorMessage}</p>
          </div>
        </div> */}
      </div>
    </>
  );
};

export default Home;
