import React from 'react';

import Switch from '@material-ui/core/Switch';
// import TextField from '@material-ui/core/TextField';
import { TimePicker } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';

import './Cycle.css';

const Cycle = (props) => {
  const { cycleNumber, isActive, updateRound, startTime, endTime } = props;

  return (
    <MuiPickersUtilsProvider utils={DateFnsUtils}>
      <div className="cycle-container">
        <div className="cycle-header">
          <h2 className="round-text">Round {cycleNumber}</h2>
          <Switch
            className="cycle-switch"
            color="primary"
            checked={isActive}
            onChange={() => {
              updateRound(`round${cycleNumber}`, 'isActive', !isActive);
            }}
          />
        </div>
        <div className="time-pickers-wrapper">
          <TimePicker
            id={`time-picker-start-cycle-${cycleNumber || ''}`}
            className="time-picker"
            label="Start Time"
            // type="time"
            ampm={false}
            value={startTime || ''}
            // InputLabelProps={{
            //   shrink: true,
            // }}
            onChange={(event) => {
              updateRound(
                `round${cycleNumber}`,
                'startTime',
                event.target.value === '' ? null : `${event.target.value}`,
              );
            }}
          />
          <TimePicker
            id={`time-picker-end-cycle-${cycleNumber || ''}`}
            className="time-picker"
            label="End Time"
            // type="time"
            value={endTime || ''}
            // InputLabelProps={{
            //   shrink: true,
            // }}
            onChange={(event) => {
              updateRound(
                `round${cycleNumber}`,
                'endTime',
                event.target.value === '' ? null : `${event.target.value}`,
              );
            }}
          />
        </div>
      </div>
    </MuiPickersUtilsProvider>
  );
};

export default Cycle;
