import React, { useState } from 'react';

import { Button } from '@material-ui/core';

import firebase from '../utils/Firebase';

import Popup from '../model/Popup';
import Week from "../components/Week";
import Cycle from '../components/Cycle';

import './MainPage.css';

const MainPage = () => {

    const [data, setData] = useState({
        sunday: false,
        monday: false,
        tuesday: false,
        wednesday: false,
        thursday: false,
        friday: false,
        saturday: false,
        firstRoundStart: null,
        firstRoundEnd: null,
        secondRoundStart: null,
        secondRoundEnd: null,
        thirdRoundStart: null,
        thirdRoundEnd: null,
        isFirstRoundActive: false,
        isSecondRoundActive: false,
        isThirdRoundActive: false
    });

    const dataCallback = (childData) => {
        setData(childData);
    }

    const weekData = {
        sunday: data.sunday,
        monday: data.monday,
        tuesday: data.tuesday,
        wednesday: data.wednesday,
        thursday: data.thursday,
        friday: data.friday,
        saturday: data.saturday
    }

    const rounds = {
        round1: {
            isActive: data.isFirstRoundActive,
            startTime: data.firstRoundStart,
            endTime: data.firstRoundEnd
        },
        round2: {
            isActive: data.isSecondRoundActive,
            startTime: data.secondRoundStart,
            endTime: data.secondRoundEnd
        },
        round3: {
            isActive: data.isThirdRoundActive,
            startTime: data.thirdRoundStart,
            endTime: data.thirdRoundEnd
        }
    }

    return (
      <React.Fragment>
        <Popup callback={dataCallback}/>
        <div className="main-container">
            <div className="title-wrapper">
                <h1>Home Irrigation System</h1>
            </div>
            <form className="main-form">
                <div className="form-body">
                    <div className="form-object-wrapper">
                        {
                            Object.keys(rounds).map((keyName, keyIndex) => {
                                return (
                                    <Cycle
                                        isActive={rounds[keyName].isActive}
                                        cycleNumber={keyIndex + 1}
                                        startTime={rounds[keyName].startTime}
                                        endTime={rounds[keyName].endTime}
                                    />
                                )
                            })
                        }
                    </div>
                    <div className="form-object-wrapper">
                        <Week week={weekData}/>
                    </div>
                </div>
                <div className="Submit-button-wrapper">
                    <Button variant="contained" color="primary">
                        Submit
                    </Button>
                </div>
            </form>
        </div>
      </React.Fragment>
    )
}

export default MainPage;
