import React, { useContext } from 'react';
import RefreshIcon from '@material-ui/icons/Refresh';
import IconButton from '@material-ui/core/IconButton';

import { UserContext } from '../providers/UserProvider';

import './Home.css';

const Home = () => {
  const user = useContext(UserContext);

  const [status, setStatus] = useState('ON');
  const [loading, setLoading] = useState('determinate');
  const [isStatusDisabled, setIsStatusDisabled] = useState(false);
  const [isBreathing, setIsBreathing] = useState(false);

  return (
    <>
      <div className="main-body">
        <div className="container left">
          <div className="container-header">Hello {user.firstName}</div>
        </div>
        <hr orientation="vertical" />
        <div className="container right">
          <div className="container-header">Status</div>
          <div
            id="breathing-status"
            className={`manual-button ${isBreathing ? 'breathing' : ''}`}
            variant="contained"
            color={status === 'START' ? 'primary' : 'secondary'}
            disabled={isSubmitDisabled}
          >
            {status}
          </div>

          <IconButton color="primary" size="medium" aria-label="refresh">
            <RefreshIcon fontSize="large" />
          </IconButton>
        </div>
      </div>
    </>
  );
};

export default Home;
