import React from 'react';

import Button from '@material-ui/core/Button';

const WatermingButton = (props) => {
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(false);

  const { isBreathing, textButton } = props;

  return (
    <div className="breathing-button-wrapper">
      <Button
        id="breathing-button"
        className={`manual-button ${isBreathing ? 'breathing' : ''}`}
        variant="contained"
        color={textButton === 'START' ? 'primary' : 'secondary'}
        type="submit"
        disabled={isSubmitDisabled}
      >
        {textButton}
      </Button>
    </div>
  );
};

export default WatermingButton;
