/* eslint-disable no-unused-vars */
/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable no-console */
import React, { useState, useContext, useEffect } from 'react';

import axios from 'axios';
import FullWidthTabs from '../components/TabFrame';
// import Popup from '../model/Popup';
// import { auth, firestore } from '../utils/Firebase';
import { auth } from '../utils/Firebase';
import { UserContext } from '../providers/UserProvider';
import { ReactComponent as BalloonSVG } from '../images/hot-air-balloon.svg';

import './MainPage.css';

const MainPage = (props) => {
  const user = useContext(UserContext);

  const [week, setWeek] = useState();
  const [rounds, setRounds] = useState();
  const [hoverAnimation, setHoverAnimation] = useState('');

  const updateRound = (round, propToUpdate, state) => {
    const updatedRound = rounds[round];

    updatedRound[propToUpdate] = state;
    const updatedRounds = { ...rounds };
    updatedRounds[round] = updatedRound;
    setRounds(updatedRounds);
  };

  const updateDay = (day, state) => {
    const updatedWeek = { ...week };
    updatedWeek[day] = state;
    setWeek(updatedWeek);
  };

  useEffect(() => {
    const { urlCode } = user;
    axios
      .get(`${urlCode}/get_week`, {
        timeout: 10 * 1000,
      })
      .then((response) => {
        console.log('login successfully');
        setWeek({
          sunday: response.sunday,
          monday: response.monday,
          tuesday: response.tuesday,
          wednesday: response.wednesday,
          thursday: response.thursday,
          friday: response.friday,
          saturday: response.saturday,
        });
        setRounds({
          round1: {
            isActive: response.isFirstRoundActive,
            startTime: response.firstRoundStart,
            endTime: response.firstRoundEnd,
          },
          round2: {
            isActive: response.isSecondRoundActive,
            startTime: response.secondRoundStart,
            endTime: response.secondRoundEnd,
          },
          round3: {
            isActive: response.isThirdRoundActive,
            startTime: response.thirdRoundStart,
            endTime: response.thirdRoundEnd,
          },
        });
        return response.data;
      })
      .catch((error) => {
        // this.errorHandler(
        //   "Connection error: please check raspberry's internet connection",
        // );
        console.log(error);
      });
    logout();
    return null;
  }, []);

  return (
    <>
      {/* <Popup
        auth={auth}
        firestore={firestore}
        setData={setData}
        key={`popup-${user}`}
        user={user}
      /> */}
      <div className="main-container">
        <div className="title-wrapper">
          <h1>Home Irrigation System</h1>
        </div>
        <FullWidthTabs
          rounds={rounds}
          updateRound={updateRound}
          week={week}
          updateDay={updateDay}
          urlCode={user.urlCode}
          sessionExpired={logout}
        />
      </div>
      <BalloonSVG
        className={`signout ${hoverAnimation}`}
        onClick={logout}
        onMouseEnter={() => {
          setHoverAnimation('balloon-animation');
        }}
        onAnimationEnd={() => {
          setHoverAnimation('');
        }}
      />
    </>
  );
};

export default MainPage;
